import axios from "axios";

// export const token = axios.create({
//     baseURL: "https://eu.battle.net/oauth/token",
//     params: {
//         client_id: "5af01d7ea23d49449a1967cb41f61859",
//         client_secret: "xVPqzY7FBA8TjxbXTp5w8Mea42NAlxwM",
//         grant_type: "client_credentials",
//     },
// });

export const token = axios.get("https://eu.battle.net/oauth/token", {
    params: {
        client_id: process.env.REACT_APP_CLIENT_ID,
        client_secret: process.env.REACT_APP_CLIENT_SECRET,
        grant_type: "client_credentials",
    },
});
// return response.data.access_token;

export const endpoint = axios.create({
    baseURL: "https://eu.api.blizzard.com/hearthstone",
});
