import axios from "axios";

export const getToken = axios.create({
    baseURL: "https://us.battle.net/oauth/token",
    params: {
        client_id: "5af01d7ea23d49449a1967cb41f61859",
        client_secret: "xVPqzY7FBA8TjxbXTp5w8Mea42NAlxwM",
        grant_type: "client_credentials",
    },
});

export const endpoint = axios.create({
    baseURL: "https://eu.api.blizzard.com/hearthstone",
    headers: { Authorization: "Bearer US3jHDpOYNEJ42P1cEee5Wf0YVrREwdr0S" },
});
