import React from "react";
import axios from "axios";

import { token, endpoint } from "../../api/blizzard";

class GamesList extends React.Component {
    state = { cards: [] };

    getAuthToken = async () => {
        const response = await axios.get("https://eu.battle.net/oauth/token", {
            params: {
                client_id: "5af01d7ea23d49449a1967cb41f61859",
                client_secret: "xVPqzY7FBA8TjxbXTp5w8Mea42NAlxwM",
                grant_type: "client_credentials",
            },
        });
        return response.data.access_token;
    };

    handleSaveToPC = (jsonData) => {
        const fileData = JSON.stringify(jsonData);
        const blob = new Blob([fileData], { type: "text/plain" });
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.download = "filename.json";
        link.href = url;
        link.click();
    };

    componentDidMount = async () => {
        const token = await this.getAuthToken();
        console.log(token);

        const response = await endpoint.get("/cards");
        this.setState({ cards: response });
        console.log(response);
        this.handleSaveToPC(response);
    };

    render() {
        return <div>HS</div>;
    }
}

export default GamesList;
